`if ($(this._dialog).hasClass(ClassName$5.SCROLLABLE)) {
        if(this._dialog.querySelector(Selector$5.MODAL_BODY)) {
          this._dialog.querySelector(Selector$5.MODAL_BODY).scrollTop = 0;
